void handle_response_output_item_done(aigw_ws_response_output_item_t *output_item, void *user_data){
    if(output_item->function_call_id){
        //notify tool call
        printf("Received function call id: %s\n", output_item->function_call_id);
        printf("Received function call name: %s\n", output_item->function_name);
        printf("Received function call arguments: %s\n", output_item->function_arguments);

        audio_mcp_context_t* ctx = (audio_mcp_context_t*)user_data;
        tool_result_t *tool_result = mcp_tool_call(ctx->mcp_ctx, output_item->function_name, output_item->function_arguments);
        if(tool_result){
            aigw_ws_conversation_item_create_t item = {0};
            item.function_call_id = output_item->function_call_id;
            char *result_text = mcp_get_tool_result_text(tool_result);
            item.output = result_text;
            onesdk_rt_conversation_item_create(ctx->onesdk_ctx, &item);
            onesdk_rt_response_create(ctx->onesdk_ctx);
        }
    }else{
        printf("assistant: %s\n", output_item->transcript);
        repeater_responsed = true;
        strcpy(repeated_content, output_item->transcript);
    }
}


void on_transcript_done(const void *text_data, size_t text_len, void *user_data){
    printf("Received transcript done: %.*s\n", (int)text_len, (char *)text_data);
    transcript_done = true;
    strncpy(transcript, (char *)text_data, text_len);
}

