void* keepalive_thread(void *arg) {
    onesdk_ctx_t *ctx = (onesdk_ctx_t *)arg;
    onesdk_rt_session_keepalive(ctx);
    return NULL;
}

void* mcp_receive_thread(void *arg){
    if(arg == NULL){
        return NULL;
    }
    mcp_context_t *ctx = (mcp_context_t *)arg;
    block_receive_from_sse_server(ctx);
}

pthread_t mcp_receiver_run(mcp_context_t *ctx){
    pthread_t tid;
    pthread_create(&tid, NULL, mcp_receive_thread, (void*)ctx);
    return tid;
}

int main() {
    onesdk_config_t *config = calloc(1, sizeof(onesdk_config_t));
    if (NULL == config) {
        return VOLC_ERR_INIT;
    }

    // 初始化配置
    iot_basic_config_t device_config = {
        .http_host = SAMPLE_HTTP_HOST,
        .instance_id = SAMPLE_INSTANCE_ID,
        .auth_type = ONESDK_AUTH_DYNAMIC_NO_PRE_REGISTERED,
        .product_key = SAMPLE_PRODUCT_KEY,
        .product_secret = SAMPLE_PRODUCT_SECRET,
        .device_name = SAMPLE_DEVICE_NAME,
        .device_secret = SAMPLE_DEVICE_SECRET,
        .verify_ssl = true,
        .ssl_ca_cert = digicert_global_root_g2,
    };
    config->device_config = &device_config;
    config->aigw_path = "/v1/realtime?model=AG-voice-chat-agent";
    config->send_ping = true;
    // config->ping_interval_s = 5;

    onesdk_rt_event_cb_t event_cb_asr = {
        .on_error = error_callback,
        .on_transcript_done = on_transcript_done,
    };

    // 初始化onesdk
    onesdk_ctx_t *onesdk_ctx_asr = calloc(1, sizeof(onesdk_ctx_t));
    if (NULL == onesdk_ctx_asr) {
        return VOLC_ERR_INIT;
    }

    int ret = onesdk_init(onesdk_ctx_asr, config);
    if (ret != 0) {
        return ret;
    }

    onesdk_rt_set_event_cb(onesdk_ctx_asr, &event_cb_asr);
    ret = onesdk_connect(onesdk_ctx_asr);
    if (ret!= 0) {
        return ret;
    }
    // create a keep alive thread
    pthread_t asr_keep_alive_thread;
    pthread_create(&asr_keep_alive_thread, NULL, keepalive_thread, onesdk_ctx_asr);

    aigw_ws_session_t asr_session;
    memset(&asr_session, 0, sizeof(aigw_ws_session_t)); // 初始化结构体
    create_asr_session(&asr_session);
    onesdk_rt_session_update(onesdk_ctx_asr, &asr_session);


    onesdk_rt_event_cb_t event_cb_repeater = {
        .on_error = error_callback,
        .on_response_output_item_done = handle_response_output_item_done,
        .on_response_done = done_callback,
    };

    onesdk_ctx_t *onesdk_ctx_repeater = calloc(1, sizeof(onesdk_ctx_t));
    if (NULL == onesdk_ctx_repeater) {
        return VOLC_ERR_INIT;
    }

    ret = onesdk_init(onesdk_ctx_repeater, config);
    if (ret != 0) {
        return ret;
    }

    onesdk_rt_set_event_cb(onesdk_ctx_repeater, &event_cb_repeater);
    ret = onesdk_connect(onesdk_ctx_repeater);
    if (ret!= 0) {
        return ret;
    }

    // create a keep alive thread
    pthread_t repeater_keep_alive_thread;
    pthread_create(&repeater_keep_alive_thread, NULL, keepalive_thread, onesdk_ctx_repeater);

    aigw_ws_session_t repeater_session;
    memset(&repeater_session, 0, sizeof(aigw_ws_session_t)); // 初始化结构体
    create_repeater_session(&repeater_session);
    onesdk_rt_session_update(onesdk_ctx_repeater, &repeater_session);


    //跟大模型进行对话
    onesdk_ctx_t *onesdk_ctx_chat = calloc(1, sizeof(onesdk_ctx_t));
    if (NULL == onesdk_ctx_chat) {
        return VOLC_ERR_INIT;
    }

    ret = onesdk_init(onesdk_ctx_chat, config);
    if (ret != 0) {
        return ret;
    }

    //启动sse接收线程
    mcp_context_t *ctx = mcp_ctx_new();
    mcp_receiver_init(ctx,SAMPLE_MCP_SERVER_IP, SAMPLE_MCP_SERVER_PORT);//mcp server 地址
    pthread_t tid = mcp_receiver_run(ctx);
    tools_list_t *tools_list = mcp_tools_list(ctx);
    if(!tools_list){
        fprintf(stderr, "mcp_tools_list failed");
        return -1;
    }
    int tools_num = tools_list->count;
    chat_tool_t *tools = (chat_tool_t *)malloc(sizeof(chat_tool_t) * tools_num);
    for (int i = 0; i < tools_num; i++) {
        tools[i].type = strdup("function");
        tools[i].function.name = strdup(tools_list->tools[i].name);
        tools[i].function.description = strdup(tools_list->tools[i].description);
        tools[i].function.parameters = strdup(tools_list->tools[i].input_schema_raw);
    }
    mcp_tools_list_free(tools_list);
    tools_list = NULL;

    onesdk_rt_audio_send(onesdk_ctx_asr, sample_audio, strlen(sample_audio), true);
    int delay_cnt = 20;
    while (!transcript_done && delay_cnt > 0) {
        sleep(1);
        delay_cnt--;
        if(delay_cnt == 0){
            printf("transcript done timeout\n");
            return -1;
        }
    }

    ChatHistory history = {0};
    char *input = transcript;
    add_user_content_to_history(&history, input);

    onesdk_chat_response_t *response = NULL;
    onesdk_chat_set_functioncall_data(onesdk_ctx_chat, &response);
    onesdk_chat_request_t request = {
        .model = model,
        .tools = tools,                     // 新增工具数组
        .tools_count = tools_num,
        .tool_choice = "auto",              // 新增工具选择策略
        .stream = false,                    // 当前，function call本次调用，仅支持非流式
        .temperature = .9,
    };
    char output[2048] = {0};
    while (true)
    {
        request.messages = history.messages;
        request.messages_count = history.count;
        memset(output, 0, sizeof(output));
        size_t output_len = sizeof(output);
        int result = onesdk_chat(onesdk_ctx_chat, &request, output, &output_len);
        // TODO 获取函数调用参数，并进行本地函数调用
        if (result) {
            fprintf(stderr, "chat request failed with code5 %d\n", result);
            goto failed;
            return result;
        }

        // 等待异步请求完成（流式）
        result = onesdk_chat_wait(onesdk_ctx_chat);
        if (result != 0) {
            fprintf(stderr, "function call request failed with code:%d. see previous output for more.\n", result);
            goto failed;
        }
        // 在第一次请求完成后添加处理逻辑
        if (result != 0 || response == NULL) {
            printf("response is null\n");
            goto failed;
        }

        if(response->choices[0].message.tool_calls_count == 0){
            printf("no tools call\n");
            break;
        }
        add_llm_response_to_history(&history, response);
        // 处理工具调用结果
        for (int i = 0; i < response->choices[0].message.tool_calls_count; i++) {
            chat_tool_call_t *tool_call = &response->choices[0].message.tool_calls[i];
            printf("Tool ID: %s\n", tool_call->id);
            printf("Tool Type: %s\n", tool_call->type);
            printf("Tool Function Name: %s\n", tool_call->function.name);
            printf("Tool Function Arguments: %s\n", tool_call->function.arguments);
            tool_result_t * tool_result = mcp_tool_call(ctx, tool_call->function.name, tool_call->function.arguments);;
            add_tool_result_to_history(&history, tool_call->id, tool_result);
        }
    }
    printf("assistant content %s\n", output);

    // 发送请求
    aigw_ws_conversation_item_create_t item = {0};
    item.text = strdup(output);
    onesdk_rt_conversation_item_create(onesdk_ctx_repeater, &item);
    free(item.text);
    onesdk_rt_response_create(onesdk_ctx_repeater);

    delay_cnt = 120;
    while (!repeater_responsed && delay_cnt > 0) {
        sleep(1);
        delay_cnt--;
        if(delay_cnt == 0){
            printf("repeater responsed timeout\n");
            return -1;
        }
    }

    printf("repeater content %s\n", repeated_content);
failed:
    // 释放历史数据
    for (int i = 0; i < history.count; i++) {
        free((void*)history.messages[i].role);
        if(history.messages[i].content){
            free((void*)history.messages[i].content);
        }
        if(history.messages[i].tool_calls_count > 0){
            for(int j = 0; j < history.messages[i].tool_calls_count; j++){
                free((void*)history.messages[i].tool_calls[j].id);
                free((void*)history.messages[i].tool_calls[j].type);
                free((void*)history.messages[i].tool_calls[j].function.name);
                free((void*)history.messages[i].tool_calls[j].function.arguments);
            }
            free((void*)history.messages[i].tool_calls);
        }
    }
    if(onesdk_ctx_chat){
        onesdk_deinit(onesdk_ctx_chat);
        free(onesdk_ctx_chat);
        onesdk_ctx_chat = NULL;
    }

    http_close(ctx->http_ctx);;
    pthread_join(tid, NULL);
    http_ctx_release(ctx->http_ctx);
    mcp_ctx_free(ctx);

    // 释放keepalive线程
    pthread_cancel(asr_keep_alive_thread);
    pthread_join(asr_keep_alive_thread, NULL);

    // 释放上下文
    if(onesdk_ctx_asr){
        onesdk_deinit(onesdk_ctx_asr);
        free(onesdk_ctx_asr);
        onesdk_ctx_asr = NULL;
    }

    // 释放keepalive线程
    pthread_cancel(repeater_keep_alive_thread);
    pthread_join(repeater_keep_alive_thread, NULL);

    // 释放repeater上下文
    if(onesdk_ctx_repeater){
        onesdk_deinit(onesdk_ctx_repeater);
        free(onesdk_ctx_repeater);
        onesdk_ctx_repeater = NULL;
    }
    free(config);
    config = NULL;
    return 0;
}
